mapper.select:

SELECT 

FROM CFPA_NEWS t 
WHERE t.DELETE_FLAG = 'N' AND  t.xwid = #{value}

mapper.insert:
INSERT INTO CFPA_NEWS(xwid,xwbt,xwbt_eng,xwnr,xwnr_eng,xwsj,xwlx,gjc,iszd,isxs,issy,delete_flag,cjrid,cjrmc,cjsj,xgrid,xgrmc,xgsj,reserve1,reserve2,reserve3,reserve4) SELECT SYS_GUID() xwid, A.* FROM (
SELECT #{item.xwid,jdbcType=VARCHAR} xwid,#{item.xwbt,jdbcType=VARCHAR} xwbt,#{item.xwbtEng,jdbcType=VARCHAR} xwbt_eng,#{item.xwnr,jdbcType=VARCHAR} xwnr,#{item.xwnrEng,jdbcType=VARCHAR} xwnr_eng,#{item.xwsj,jdbcType=DATE} xwsj,#{item.xwlx,jdbcType=VARCHAR} xwlx,#{item.gjc,jdbcType=VARCHAR} gjc,#{item.iszd,jdbcType=DOUBLE} iszd,#{item.isxs,jdbcType=VARCHAR} isxs,#{item.issy,jdbcType=VARCHAR} issy,'N' delete_flag,#{item.cjrid,jdbcType=VARCHAR} cjrid,#{item.cjrmc,jdbcType=VARCHAR} cjrmc,#{item.cjsj,jdbcType=DATE} cjsj,#{item.xgrid,jdbcType=VARCHAR} xgrid,#{item.xgrmc,jdbcType=VARCHAR} xgrmc,#{item.xgsj,jdbcType=DATE} xgsj,#{item.reserve1,jdbcType=VARCHAR} reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=VARCHAR} reserve3,#{item.reserve4,jdbcType=DATE} reserve4 FROM DUAL
) A

mapper.update:
UPDATE CFPA_NEWS
xwid=#{xwid}, xwbt=#{xwbt}, xwbt_eng=#{xwbtEng}, xwnr=#{xwnr}, xwnr_eng=#{xwnrEng}, xwsj=TO_DATE(#{xwsj},'yyyy-MM-dd HH24:mi:ss'), xwlx=#{xwlx}, gjc=#{gjc}, iszd=#{iszd}, isxs=#{isxs}, issy=#{issy}, delete_flag='N', cjrid=#{cjrid}, cjrmc=#{cjrmc}, cjsj=TO_DATE(#{cjsj},'yyyy-MM-dd HH24:mi:ss'), xgrid=#{xgrid}, xgrmc=#{xgrmc}, xgsj=TO_DATE(#{xgsj},'yyyy-MM-dd HH24:mi:ss'), reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=#{reserve3}, reserve4=TO_DATE(#{reserve4},'yyyy-MM-dd HH24:mi:ss')
WHERE xwid = #{xwid}
