mapper.select:

SELECT 

FROM CFPA_QY_JS t 
WHERE t.DELETE_FLAG = 'N' AND  t.QYID = #{value}

mapper.insert:
INSERT INTO CFPA_QY_JS(uuid,qyid,qyjj,delete_flag,cjrid,cjrmc,cjsj,xgrid,xgrmc,xgsj,reserve1,reserve2,reserve3,reserve4,src) SELECT SYS_GUID() uuid, A.* FROM (
SELECT #{item.uuid,jdbcType=VARCHAR} uuid,#{item.qyid,jdbcType=VARCHAR} qyid,#{item.qyjj,jdbcType=VARCHAR} qyjj,'N' delete_flag,#{item.cjrid,jdbcType=VARCHAR} cjrid,#{item.cjrmc,jdbcType=VARCHAR} cjrmc,#{item.cjsj,jdbcType=DATE} cjsj,#{item.xgrid,jdbcType=VARCHAR} xgrid,#{item.xgrmc,jdbcType=VARCHAR} xgrmc,#{item.xgsj,jdbcType=DATE} xgsj,#{item.reserve1,jdbcType=VARCHAR} reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=VARCHAR} reserve3,#{item.reserve4,jdbcType=DATE} reserve4,#{item.src,jdbcType=VARCHAR} src FROM DUAL
) A

mapper.update:
UPDATE CFPA_QY_JS
qyid=#{qyid},
qyjj=#{qyjj}, delete_flag='N', cjrid=#{cjrid}, cjrmc=#{cjrmc}, cjsj=TO_DATE(#{cjsj},'yyyy-MM-dd HH24:mi:ss'), xgrid=#{xgrid}, xgrmc=#{xgrmc}, xgsj=sysdate, reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=#{reserve3}, reserve4=TO_DATE(#{reserve4},'yyyy-MM-dd HH24:mi:ss'), src=#{src}
WHERE UUID = #{uuid}

UPDATE CFPA_QY_JS
delete_flag='Y', xgrid=#{xgrid}, xgrmc=#{xgrmc}, xgsj=sysdate
WHERE QYID = #{qyid}
