mapper.insert:
INSERT INTO
CFPA_QY_WJDC(uuid,qyid,gsxz,sfhwdlcp,hwdlcppp,fmzl,syxxzl,wgsjzl,sfgxjsqy,gxjsjb,zycp,sfhyxydj,hyxydj,delete_flag,cjrid,cjrmc,cjsj,xgrid,xgrmc,xgsj,reserve1,reserve2,reserve3,reserve4)
SELECT SYS_GUID() uuid, A.*
FROM (

    SELECT
    #{item.uuid,jdbcType=VARCHAR} uuid,#{item.qyid,jdbcType=VARCHAR} qyid,#{item.gsxz,jdbcType=VARCHAR}
    gsxz,#{item.sfhwdlcp,jdbcType=VARCHAR} sfhwdlcp,#{item.hwdlcppp,jdbcType=VARCHAR}
    hwdlcppp,#{item.fmzl,jdbcType=DOUBLE} fmzl,#{item.syxxzl,jdbcType=DOUBLE}
    syxxzl,#{item.wgsjzl,jdbcType=DOUBLE} wgsjzl,#{item.sfgxjsqy,jdbcType=VARCHAR}
    sfgxjsqy,#{item.gxjsjb,jdbcType=VARCHAR} gxjsjb,#{item.zycp,jdbcType=VARCHAR}
    zycp,#{item.sfhyxydj,jdbcType=VARCHAR} sfhyxydj,#{item.hyxydj,jdbcType=VARCHAR} hyxydj,'N'
    delete_flag,#{item.cjrid,jdbcType=VARCHAR} cjrid,#{item.cjrmc,jdbcType=VARCHAR}
    cjrmc,#{item.cjsj,jdbcType=DATE} cjsj,#{item.xgrid,jdbcType=VARCHAR} xgrid,#{item.xgrmc,jdbcType=VARCHAR}
    xgrmc,#{item.xgsj,jdbcType=DATE} xgsj,#{item.reserve1,jdbcType=VARCHAR}
    reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=VARCHAR}
    reserve3,#{item.reserve4,jdbcType=DATE} reserve4
    FROM DUAL

) A

mapper.update:
UPDATE CFPA_QY_WJDC
qyid=#{qyid}, gsxz=#{gsxz}, sfhwdlcp=#{sfhwdlcp}, hwdlcppp=#{hwdlcppp}, fmzl=#{fmzl}, syxxzl=#{syxxzl}, wgsjzl=#{wgsjzl}, sfgxjsqy=#{sfgxjsqy}, gxjsjb=#{gxjsjb}, zycp=#{zycp}, sfhyxydj=#{sfhyxydj}, hyxydj=#{hyxydj}, delete_flag='N', cjrid=#{cjrid}, cjrmc=#{cjrmc}, cjsj=TO_DATE(#{cjsj},'yyyy-MM-dd HH24:mi:ss'), xgrid=#{xgrid}, xgrmc=#{xgrmc}, xgsj=sysdate, reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=#{reserve3}, reserve4=TO_DATE(#{reserve4},'yyyy-MM-dd HH24:mi:ss')
WHERE UUID = #{uuid}

UPDATE CFPA_QY_WJDC
delete_flag='Y', xgrid=#{xgrid}, xgrmc=#{xgrmc}, xgsj=sysdate
WHERE QYID = #{qyid}
