mapper.select:

SELECT 

FROM CFPA_ZG_JBXX t 
WHERE t.DELETE_FLAG = 'N' AND  t.UUID = #{value}

mapper.insert:
INSERT INTO CFPA_ZG_JBXX(uuid,zgtp,zgzwhbtp,zgzwhb,zgmc,zgtpdz,delete_flag,cjrid,cjrmc,cjsj,xgrid,xgrmc,xgsj,reserve1,reserve2,reserve3,reserve4) SELECT SYS_GUID() uuid, A.* FROM (
SELECT #{item.uuid,jdbcType=VARCHAR} uuid,#{item.zgtp,jdbcType=VARCHAR} zgtp,#{item.zgzwhbtp,jdbcType=VARCHAR} zgzwhbtp,#{item.zgzwhb,jdbcType=VARCHAR} zgzwhb,#{item.zgmc,jdbcType=VARCHAR} zgmc,#{item.zgtpdz,jdbcType=VARCHAR} zgtpdz,'N' delete_flag,#{item.cjrid,jdbcType=VARCHAR} cjrid,#{item.cjrmc,jdbcType=VARCHAR} cjrmc,#{item.cjsj,jdbcType=DATE} cjsj,#{item.xgrid,jdbcType=VARCHAR} xgrid,#{item.xgrmc,jdbcType=VARCHAR} xgrmc,#{item.xgsj,jdbcType=DATE} xgsj,#{item.reserve1,jdbcType=VARCHAR} reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=VARCHAR} reserve3,#{item.reserve4,jdbcType=DATE} reserve4 FROM DUAL
) A

mapper.update:
UPDATE CFPA_ZG_JBXX
xgsj=sysdate, zgtp=#{zgtp}, zgzwhb=#{zgzwhb}, zgzwhbtp=#{zgzwhbtp}, zgmc=#{zgmc}, zgtpdz=#{zgtpdz}, delete_flag=#{deleteFlag}, cjrid=#{cjrid}, cjrmc=#{cjrmc}, cjsj=TO_DATE(#{cjsj},'yyyy-MM-dd HH24:mi:ss'), xgrid=#{xgrid}, xgrmc=#{xgrmc}, reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=#{reserve3}, reserve4=TO_DATE(#{reserve4},'yyyy-MM-dd HH24:mi:ss')
WHERE UUID = #{uuid}
